	private List<EntryCategory> mSubCat;
	private String mName;
	private int mId;
	
	public EntryCategory(){
		mSubCat = new ArrayList<>();
		mName = "";
		mId = 0;
	}
	public EntryCategory(int i, String n){
		mSubCat = new ArrayList<>();
		mName = n;
		mId = i;
	}
	
	public EntryCategory addSubCategory(List<EntryCategory> t){
		for(EntryCategory i : t){
			this.addSubCategory(i);
		}
		return this;
	}
	public EntryCategory addSubCategory(EntryCategory cat){
		Boolean isSet = false;
		for(int i = 0; i < mSubCat.size(); i++){
			if(mSubCat.get(i).getId() == cat.getId()){
				mSubCat.set(i, cat);
				isSet = ! isSet;
			}
		}
		if(!isSet) mSubCat.add(cat);
		
		return this;
	}
	
	public List getSubCategories(){
		return mSubCat;
	}
	
	public void setName(String t){
		mName = t;
	}
	public String getName(){
		return mName;
	}
	
	public void setId(int i){
		mId = i;
	}
	public int getId(){
		return mId;
	}
	
	
